import React, { useState } from "react";
import arrowUp from "../../images/arrow_up.png";

const Transaction = ({ transaction }) => {
  const [isOpen, setIsOpen] = useState(false);

  const formattedAmount = transaction.amount.toLocaleString("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  const formattedBalance = transaction.balance.toLocaleString("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  return (
    <div className="transaction-container" onClick={() => setIsOpen(!isOpen)}>
      <p>{transaction.date}</p>
      <p>{transaction.description}</p>
      <p>${formattedAmount}</p>
      <p>${formattedBalance}</p>
      <img
        src={arrowUp}
        alt=""
        className={isOpen ? "arrow arrow-in" : "arrow arrow-out"}
      />
      <div
        className={
          isOpen
            ? "transaction-container-content is-open"
            : "transaction-container-content"
        }
      >
        <div
          className={`transaction-container-content-text ${
            isOpen ? "transaction-container-content-open" : ""
          }`}
        >
          <div className="transaction-informations">
            <div className="transaction-informations-item">
              <p>Transaction type</p>
              <p>{transaction.type}</p>
            </div>
            <div className="transaction-informations-item"></div>
            <div className="transaction-informations-item"></div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Transaction;
