import React, { useState } from "react";

const Account = ({ title, amount, type, transactions }) => {
  const isCreditType = type;
  const formattedAmount = amount.toLocaleString("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  const [isOpen, setIsOpen] = useState(false);

  return (
    <section className="account">
      <div className="account-content-wrapper">
        <h3 className="account-title">{title}</h3>
        <p className="account-amount">${formattedAmount}</p>
        <p className="account-amount-description">
          {isCreditType === "Credit" ? "Current Balance" : "Available Balance"}
        </p>
      </div>
      <div className="account-content-wrapper cta">
        <button
          className="transaction-button"
          onClick={() => setIsOpen(!isOpen)}
        >
          View transactions
        </button>
      </div>
      <div className="transactions-container">
        {transactions.map((i, transaction) => (
          <div key={transaction.amount + `${i}`}>{transaction.description}</div>
        ))}
      </div>
    </section>
  );
};

export default Account;
