import React, { useState } from "react";
import TransactionsList from "../transactions-list/TransactionsList";

const Account = ({ account }) => {
  const isCreditType = account.type;
  const formattedAmount = account.balance.toLocaleString("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  const [isOpen, setIsOpen] = useState(false);

  return (
    <section className="account">
      <div className="account-container">
        <div className="account-content-wrapper">
          <h3 className="account-title">{account.title}</h3>
          <p className="account-amount">${formattedAmount}</p>
          <p className="account-amount-description">
            {isCreditType === "Credit"
              ? "Current Balance"
              : "Available Balance"}
          </p>
        </div>
        <div className="account-content-wrapper cta">
          <button
            className="transaction-button"
            onClick={() => setIsOpen(!isOpen)}
          >
            View transactions
          </button>
        </div>
      </div>
      <div
        className={`transactions-container ${
          isOpen ? "transactions-container-open" : ""
        }`}
      >
        {account.transactions.map((transaction, i) => (
          <TransactionsList
            key={transaction.note + " - " + i}
            transaction={transaction}
          />
        ))}
      </div>
    </section>
  );
};

export default Account;
