import React, { useState } from "react";
import arrowUp from "../../images/arrow_up.png";

const Transaction = ({ transaction }) => {
  const [isOpen, setIsOpen] = useState(false);

  const formattedAmount = transaction.amount.toLocaleString("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  const formattedBalance = transaction.balance.toLocaleString("en-US", {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  });
  return (
    <div className="transaction-container">
      <p>{transaction.date}</p>
      <p>{transaction.description}</p>
      <p>${formattedAmount}</p>
      <p>${formattedBalance}</p>
      <img
        src={arrowUp}
        alt=""
        className={
          isOpen
            ? "collapse-container__arrow-container__arrow collapse-container__arrow-container__arrow__in"
            : "collapse-container__arrow-container__arrow collapse-container__arrow-container__arrow__out"
        }
      />
    </div>
  );
};

export default Transaction;
